CREATE TABLE `?:tz_categories_docs` (
    `doc_category_id` int unsigned NOT NULL auto_increment,
    `company_id` int unsigned NOT NULL default '0',
    `status` char(1) NOT NULL default 'A',
    PRIMARY KEY  (`doc_category_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;

CREATE TABLE `?:tz_categories_docs_description` (
    `doc_category_id` int unsigned NOT NULL,
    `lang_code` char(2) NOT NULL default '',
    `category_name` varchar(255) NOT NULL,
    UNIQUE KEY `doc_category_id` (`doc_category_id`,`lang_code`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb3;
